\copy (SELECT s.id, i.atmos_oid, ssi.ext, vf.frame_num, gt_bb.annotation, gt_nose.annotation, gt_left.annotation, gt_right.annotation FROM annotations_groundtruth gt_bb LEFT JOIN annotations_groundtruth gt_nose ON gt_nose.ref_id=gt_bb.ref_id AND gt_nose.type='NoseBaseLandmark' LEFT JOIN annotations_groundtruth gt_left ON gt_left.ref_id=gt_bb.ref_id AND gt_left.type='LeftEyeLandmark' LEFT JOIN annotations_groundtruth gt_right ON gt_right.ref_id=gt_bb.ref_id AND gt_right.type='RightEyeLandmark' JOIN subject_sighting_images ssi ON ssi.id=gt_bb.ref_id JOIN subjects s ON s.id=ssi.subject_id JOIN images i ON i.id=ssi.image_id LEFT JOIN video_frames vf ON vf.id=ssi.image_id WHERE gt_bb.type='IDVerification' ORDER BY s.id, i.id) To 'DSX.csv' With CSV
